mapper.select:
select count(*) from memo_board

	where tier = #{tier}


	AND division = #{division}


	AND queueType = #{queueType}


	AND lane = #{lane}


	AND expectedTime = #{expectedTime}


	AND isVoice = #{isVoice}

select 
	Q.*	
from
	(select 
		rownum rnum, R.* 
	from
		(select * from memo_board
		
			where tier = #{tier}
		
		
			AND division = #{division}
		
		
			AND queueType = #{queueType}
		
		
			AND lane = #{lane}
		
		
			AND expectedTime = #{expectedTime}
		
		
			AND isVoice = #{isVoice}
		
		order by idx desc) R
	where
		rownum <= #{endNo}) Q
where
	rnum >= #{startNo}
